var cursor=document.querySelector(".cursor"),cursorinner=document.querySelector(".cursor2"),svg=document.querySelectorAll("svg"),img=document.querySelectorAll("img"),h3=document.querySelectorAll("h3");function progressBarScroll(){let t=(document.body.scrollTop||document.documentElement.scrollTop)/(document.documentElement.scrollHeight-document.documentElement.clientHeight)*100;document.getElementById("progressBar").style.width=t+"%"}document.addEventListener("mousemove",function(e){cursor.style.transform=`translate3d(calc(${e.clientX}px - 50%), calc(${e.clientY}px - 50%), 0)`}),document.addEventListener("mousemove",function(e){var t=e.clientY;cursorinner.style.left=e.clientX+"px",cursorinner.style.top=t+"px"}),document.addEventListener("mousedown",function(){cursor.classList.add("click"),cursorinner.classList.add("cursorinnerhover")}),document.addEventListener("mouseup",function(){cursor.classList.remove("click"),cursorinner.classList.remove("cursorinnerhover")}),svg.forEach(e=>{e.addEventListener("mouseover",()=>{cursor.classList.add("hover")}),e.addEventListener("mouseleave",()=>{cursor.classList.remove("hover")})}),img.forEach(e=>{e.addEventListener("mouseover",()=>{cursor.classList.add("hover")}),e.addEventListener("mouseleave",()=>{cursor.classList.remove("hover")})}),h3.forEach(e=>{e.addEventListener("mouseover",()=>{cursor.classList.add("hover")}),e.addEventListener("mouseleave",()=>{cursor.classList.remove("hover")})}),window.onscroll=function(){progressBarScroll()};